import { createSlice } from '@reduxjs/toolkit';
import {
	signInWithGoogle,
	createUserProfileDocument,
} from '../firebase/firebase.utils';
import { auth } from 'firebase';

export const userSlice = createSlice({
	name: 'user',
	initialState: {
		currentUser: null,
		error: null,
	},
	reducers: {
		SET_CURRENT_USER: (state, action) => {
			state.currentUser = action.payload;
		},
		GOOGLE_SIGNIN_SUCCESS: (state, action) => {
			state.currentUser = action.payload;
			state.error = null;
		},
		GOOGLE_SIGNIN_FALURE: (state, action) => {
			state.currentUser = null;
			state.error = action.payload;
		},
		SIGN_OUT: (state) => {
			state.currentUser = null;
			state.error = null;
		},
	},
});
export const {
	SET_CURRENT_USER,
	GOOGLE_SIGNIN_SUCCESS,
	GOOGLE_SIGNIN_FALURE,
	SIGN_OUT,
} = userSlice.actions;
export default userSlice.reducer;

export const googleSignin = () => async (dispatch) => {
	try {
		const { user } = await signInWithGoogle();
		const docRef = await createUserProfileDocument(user);
		const snap = await docRef.get();
		dispatch(GOOGLE_SIGNIN_SUCCESS({ id: snap.id, ...snap.data() }));
	} catch (err) {
		dispatch(GOOGLE_SIGNIN_FALURE(err.message));
	}
};
export const signOut = () => async (dispatch) => {
	try {
		await auth.signOut;
		dispatch(SIGN_OUT());
	} catch (err) {
		console.log(err.message);
	}
};

