import React, { useEffect, useState } from 'react';
import CollectionPage from '../collections/CollectionPage';
import CollectionOverview from '../../components/preview-collection/CollectionOverview';
import { useDispatch, useSelector } from 'react-redux';
import { setShopData } from '../../redux/shopSlice';
import { Route, useRouteMatch } from 'react-router-dom';
import WithSpinner from '../../components/WithSpinner/WithSpinner';
import CollectionPageContainer from '../collections/CollectionPageContainer';
const CollectionOverViewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

const Shop = () => {
	const { loading } = useSelector((state) => state.shop);
	const dispatch = useDispatch();
	useEffect(() => {
		dispatch(setShopData());
	}, [dispatch]);
	let match = useRouteMatch();
	return (
		<div>
			<Route
				exact
				path={match.path}
				render={(props) => (
					<CollectionOverViewWithSpinner isLoading={loading} {...props} />
				)}
			/>
			<Route
				exact
				path={`${match.path}/:id`}
				render={(props) => (
					<CollectionPageWithSpinner isLoading={loading} {...props} />
				)}
			/>
		</div>
	);
};

export default Shop;
