import { createSlice } from '@reduxjs/toolkit';
import { firestore } from '../firebase/firebase.utils';
const initialState = {
	loading: false,
	collections: [],
};

export const shopSlice = createSlice({
	name: 'shop',
	initialState: initialState,
	reducers: {
		FETCH_COLLECTION_START: (state, action) => {
			state.loading = true;
			//	state.collections = action.payload;
		},
		FETCH_COLLECTION_SUCCESS: () => {},
		FETCH_COLLECTION_FAILURE: () => {},
	},
});

export default shopSlice.reducer;
export const {} = shopSlice.actions;

export const setShopData = () => (dispatch) => {
	dispatch({
		type: FETCH_COLLECTION_START,
	});
	const colRef = firestore.collection('collections');
	colRef.onSnapshot(
		(snap) => {
			if (!snap.empty) {
				let documents = snap.docs.map((doc) => ({
					id: doc.id,
					routeName: doc.data().title.toLowerCase(),
					...doc.data(),
				}));
				dispatch({
					type: FETCH_COLLECTION_SUCCESS,
					payload: documents,
				});
			}
		},
		(err) => {
			console.log(`Encountered error: ${err}`);
		}
	);
};
