import { createSlice } from '@reduxjs/toolkit';
import {
	signInWithGoogle,
	createUserProfileDocument,
} from '../firebase/firebase.utils';

export const userSlice = createSlice({
	name: 'user',
	initialState: {
		currentUser: null,
	},
	reducers: {
		SET_CURRENT_USER: (state, action) => {
			state.currentUser = action.payload;
		},
		GOOGLE_SIGNIN_SUCCESS: (state, action) => {
			state.currentUser = action.payload;
		},
	},
});
export const { SET_CURRENT_USER, GOOGLE_SIGNIN_SUCCESS } = userSlice.actions;
export default userSlice.reducer;

// const dispatcher = (dispatch) => {
// 	return;
// };
const googleSignInSuccess = (dispatch, user) => {
	dispatch(GOOGLE_SIGNIN_SUCCESS(user));
};
export const signinWithGoogle = () => {
	signInWithGoogle()
		.then((ref) => ref.user)
		.then((user) => createUserProfileDocument(user))
		.then((userRef) => userRef.get())
		.then((snapShot) =>
			googleSignInSuccess({ id: snapShot.id, ...snapShot.data() })
		)
		.catch((err) => console.log(err));

	// const { user } = await signInWithGoogle();
	// const userRef = await createUserProfileDocument(user);
	// const snapShot = await userRef.get();
	// googleSignInSuccess({ id: snapShot.id, ...snapShot.data() });
};
