import { createSlice } from '@reduxjs/toolkit';
import {
	googleProvider,
	auth,
	firestore,
	createUserProfileDocument,
} from '../firebase/firebase.utils';
export const userSlice = createSlice({
	name: 'user',
	initialState: {
		currentUser: null,
		error: '',
	},
	reducers: {
		SET_CURRENT_USER: (state, action) => {
			state.currentUser = action.payload;
		},
		GOOGLE_SIGNIN_SUCCESS: (state, action) => {
			state.currentUser = action.payload;
			state.error = '';
		},
		GOOGLE_SIGNIN_FAILURE: () => {},
		SIGNIN_USER_PASSWORD: () => {},
		SIGNIN_FAIL: () => {},

		SIGN_OUT: () => {},
	},
});
export const { SET_CURRENT_USER, GOOGLE_SIGNIN_SUCCESS } = userSlice.actions;
export default userSlice.reducer;

export const signInWithGoogle = () => async (dispatch) => {
	const { user } = await auth.signInWithPopup(googleProvider);
	const docRef = await createUserProfileDocument(user, {});
	const snap = await docRef.get();

	dispatch(GOOGLE_SIGNIN_SUCCESS(snap.data()));
};
